  // array/object 담은 변수엔 화살표만 저장됨
  // ... -> 괄호를 벗겨주세요

  return(
    // jsx 언어임
    <div className="App">      
      <div className = "black-nav">
        <h4 style = {{color:'red', fontSize: '20px'}}>{글제목[0]}</h4>  
      </div>            

      {
        글제목.map(function(a, i){
          return (
            <div className="list">
              <div>{a[i]}</div>
            </div>
          )
        })
      }

    </div>
  );
}

